(1, 'Gabriel Rodríguez', 'web@hablemosdesalud.com.co', '$2y$10$EY2IF/a3VZcP2fOf8qtpw.lUp4JPpC5oIJaooz/y2M0cCtlOPcc9m', NULL, '0000-00-00 00:00:00', '2017-04-11 15:29:30'),
(2, 'Angélica Sandoval', 'promocionyprevencion@hablemosdesalud.com.co', '$2y$10$7CHOU4K2y3/EjnO/3ruHXukE8ne/UgdHOS.nslovETfeCfhPkW94.', NULL, '2017-04-11 15:29:54', '2017-04-11 15:29:54');
